        reviewInfoMap.put("scoreId",-1);
        reviewInfoMap.put("hitId",hitInfo.getReviewInfo().getHitId());
        reviewInfoMap.put("rid",getReviewInfoRid());
        reviewInfoMap.put("reviewResult",1);
